function PageItem(props) {
  const { active, disabled, children } = props;

  const handleClickSelectPage = (ev) => {
    if (ev.target.innerText) setPage(+ev.target.innerText);
  };
  return (
    <li className={`page-item ${active ? "active" : ""} ${disabled ? "disabled" : ""}`}>
      <button className="page-link shadow-none" value={children} onClick={handleClickSelectPage}>
        {children}
      </button>
    </li>
  );
}
export default PageItem;
