function validationRegister(email, username, phoneNumber) {
  const newError = {};
  if (!email) newError.email = "Email is required";
  if (!username) newError.username = "Username is required";
  if (!phoneNumber) newError.phoneNumber = "Phone number is required";
  else if (isNaN(+phoneNumber)) newError.phoneNumber = "Phone number is invalid";
  else if (phoneNumber.length !== 10) newError.phoneNumber = "Phone number length is invalid";

  return newError;
}

export { validationRegister };
