import { useState } from "react";
import Button from "../ui/Button";
import { validationRegister } from "./validation";

function TodoInput(props) {
  const [input, setInput] = useState({
    email: "",
    username: "",
    phoneNumber: ""
  });

  const [error, setError] = useState({
    email: "",
    username: "",
    phoneNumber: ""
  });

  const handleChangeInput = (ev) => {
    const cloneInput = { ...input };
    cloneInput[ev.target.id] = ev.target.value;
    setInput(cloneInput);
  };

  const handleSubmitForm = (ev) => {
    ev.preventDefault();
    const cloneError = {};

    cloneError.email = !input.email ? "Email is required" : "";
    cloneError.username = !input.username ? "Username is required" : "";

    if (!input.phoneNumber) cloneError.phoneNumber = "Phone number is required";
    else if (isNaN(+input.phoneNumber)) cloneError.phoneNumber = "Phone number is invalid";
    else if (input.phoneNumber.length !== 10) cloneError.phoneNumber = "Phone number length is invalid";
    else cloneError.phoneNumber = "";
    if (Object.keys(cloneError).length > 0) setError(cloneError);
  };

  return (
    <form className="form-input" onSubmit={handleSubmitForm}>
      <div className="mb-3">
        <label htmlFor="email" className="form-label">
          Email address
        </label>
        <input
          type="email"
          className="form-control"
          id="email"
          value={input.email}
          onChange={handleChangeInput}
        ></input>
        {error.email && <small className="text-danger">{error.email}</small>}
      </div>

      <div className="mb-3">
        <label htmlFor="username" className="form-label">
          Username
        </label>
        <input type="text" className="form-control" id="username" onChange={handleChangeInput}></input>
        {error.username && <small className="text-danger">{error.username}</small>}
      </div>

      <div className="mb-3">
        <label htmlFor="phoneNumber" className="form-label">
          Phone Number
        </label>
        <input type="text" className="form-control" id="phoneNumber" onChange={handleChangeInput}></input>
        {error.phoneNumber && <small className="text-danger">{error.phoneNumber}</small>}
      </div>
      <button type="submit" className="btn btn-primary">
        Submit
      </button>
      <button type="cancel" className="ms-3 btn btn-success">
        Cancel
      </button>
    </form>
  );
}
export default TodoInput;
