import PageItem from "./PageItem";
import { v4 as uuidv4 } from "uuid";
function PageList(props) {
  const { filteredTodoList, page } = props;

  const createButtonList = () => {
    let arr = [];
    for (let i = 1; i <= Math.ceil(filteredTodoList.length / page.pagelimit); i++) {
      arr.push(<PageItem {...props}>{i}</PageItem>);
    }
    return arr;
  };

  return (
    <ul className="pagination pagination-sm">
      <PageItem {...props} id="back">
        <i className="fa-solid fa-angle-left" />
      </PageItem>
      {createButtonList()}

      {/* <PageItem {...props}>1</PageItem>
      <PageItem {...props}>2</PageItem>
      <PageItem {...props}>3</PageItem> */}

      <PageItem {...props} id="next" key={uuidv4()}>
        {" "}
        <i className="fa-solid fa-angle-right" />
      </PageItem>
    </ul>
  );
}
export default PageList;
