import { v4 as uuidv4 } from "uuid";
import { useState } from "react";

import TodoInput from "./components/todo-list/TodoInput";
import Filter from "./components/filter/Filter";
import PageLimit from "./components/page-limit/PageLimit";
import TodoList from "./components/todo-list/TodoList";
import Pagination from "./components/pagination/Pagination";

const initialTodoList = [
  { title: "Sport", completed: true, id: uuidv4() },
  { title: "Gaming", completed: false, id: uuidv4() },
  { title: "Entertain1", completed: true, id: uuidv4() },
  { title: "Entertain2", completed: true, id: uuidv4() },
  { title: "Entertain3", completed: true, id: uuidv4() },
  { title: "Entertain4", completed: true, id: uuidv4() },
  { title: "Entertain5", completed: true, id: uuidv4() },
  { title: "Entertain6", completed: true, id: uuidv4() },
  { title: "Entertain7", completed: true, id: uuidv4() },
  { title: "Entertain8", completed: true, id: uuidv4() },
  { title: "Entertain9", completed: true, id: uuidv4() },
  { title: "Entertain10", completed: true, id: uuidv4() },
  { title: "Entertain11", completed: true, id: uuidv4() },
  { title: "Entertain12", completed: true, id: uuidv4() },
  { title: "Entertain13", completed: true, id: uuidv4() },
  { title: "Entertain14", completed: true, id: uuidv4() },
  { title: "Entertain15", completed: true, id: uuidv4() }
];

function App() {
  const [todoList, setTodoList] = useState(initialTodoList);
  const [searchStatus, setSearchStatus] = useState(null);

  const createTodo = (title) => {
    const newTodo = { title, completed: false, id: uuidv4() };
    const oldTodolist = [...todoList];
    oldTodolist.unshift(newTodo);
    setTodoList(oldTodolist);
  };

  const removeTodo = (id) => {
    const idx = todoList.findIndex((item) => item.id === id);
    if (idx !== -1) {
      const cloneTodoList = [...todoList];
      cloneTodoList.splice(idx, 1);
      setTodoList(cloneTodoList);
    }
  };

  const updateTodo = (newValue, id) => {
    const idx = todoList.findIndex((item) => item.id === id);
    if (idx !== -1) {
      const cloneTodoList = [...todoList];
      cloneTodoList[idx] = { ...todoList[idx], ...newValue }; // เป็นการ Merger object โดยใช้ Destructuring โดยมันจะทำการ Update เฉพาะ key ที่ส่งเข้ามา
      setTodoList(cloneTodoList);
    }
  };

  const changeSearchStatus = (value) => {
    setSearchStatus(value);
  };

  const filteredTodoList = todoList.filter((el) => searchStatus === null || el.completed === searchStatus);

  return (
    <div className="container max-w-xs pt-5">
      <TodoInput createTodo={createTodo} />
      <Filter changeSearchStatus={changeSearchStatus} searchStatus={searchStatus} filteredTodoList={filteredTodoList} />
      <PageLimit />
      <TodoList
        filteredTodoList={filteredTodoList}
        setTodoList={setTodoList}
        removeTodo={removeTodo}
        updateTodo={updateTodo}
        searchStatus={searchStatus}
      />
      <Pagination />
    </div>
  );
}

export default App;
