import RadioButton from "../ui/RadioButton";
function FilterStatus(props) {
  const { changeSearchStatus, searchStatus } = props;
  return (
    <div className="btn-group ms-3" role="group" aria-label="Basic radio toggle button group">
      <RadioButton
        name="status"
        id="all"
        color="outline-dark"
        onChange={() => changeSearchStatus("ALL")}
        defaultCheck={searchStatus === "ALL"}
      >
        <i className="fa-solid fa-list" />
      </RadioButton>
      <RadioButton
        name="status"
        id="completed"
        color="outline-dark"
        onChange={() => changeSearchStatus("COMPLETED")}
        defaultCheck={searchStatus === "COMPLETED"}
      >
        <i className="fa-solid fa-clipboard-check" />
      </RadioButton>
      <RadioButton
        name="status"
        id="pending"
        color="outline-dark"
        onChange={() => changeSearchStatus("PENDING")}
        defaultCheck={searchStatus === "PENDING"}
      >
        <i className="fa-regular fa-clipboard" />
      </RadioButton>
    </div>
  );
}
export default FilterStatus;
