import { useState } from "react";
import Button from "../ui/Button";

function TodoInput(props) {
  const [input, setInput] = useState({
    email: "",
    username: "",
    phoneNumber: ""
  });

  const [error, setError] = useState({
    email: "",
    username: "",
    phoneNumber: ""
  });

  const handleChangeInput = (ev) => {
    const cloneInput = { ...input };
    cloneInput[ev.target.id] = ev.target.value;
    setInput(cloneInput);
  };

  const handleSubmitForm = (ev) => {
    ev.preventDefault();
    const cloneError = {};

    if (!input.email) cloneError.email = "Email is required";
    if (!input.username) cloneError.username = "Username is required";

    if (isNaN(+input.phoneNumber)) cloneError.phoneNumber = "Phone number is invalid";
    else if (input.phoneNumber.length !== 10) cloneError.phoneNumber = "Phone number length is invalid";
    else if (input.phoneNumber) cloneError.phoneNumber = "Phone number is required";

    if (Object.keys(cloneError).length > 0) setError(cloneError);
  };

  const showError = (id) => {
    if (error[id]) return <small className="text-danger">{error.id}</small>;
  };

  return (
    <form className="form-input" onSubmit={handleSubmitForm}>
      <div className="mb-3">
        <label htmlFor="email" className="form-label">
          Email address
        </label>
        <input
          type="email"
          className="form-control"
          id="email"
          value={input.email}
          onChange={handleChangeInput}
        ></input>
        {showError("email")}
      </div>

      <div className="mb-3">
        <label htmlFor="username" className="form-label">
          Username
        </label>
        <input type="text" className="form-control" id="username" onChange={handleChangeInput}></input>
      </div>

      <div className="mb-3">
        <label htmlFor="phoneNumber" className="form-label">
          Phone Number
        </label>
        <input type="text" className="form-control" id="phoneNumber" onChange={handleChangeInput}></input>
      </div>
      <button type="submit" className="btn btn-primary">
        Submit
      </button>
      <button type="cancel" className="ms-3 btn btn-success">
        Cancel
      </button>
    </form>
  );
}
export default TodoInput;
