import { useState } from "react";
import Button from "../ui/Button";

function TodoInput(props) {
  const [input, setInput] = useState({
    email: "",
    username: "",
    phoneNumber: ""
  });

  const [error, setError] = useState({
    email: "",
    username: "",
    phoneNumber: ""
  });

  return (
    <form>
      <div className="mb-3">
        <label htmlFor="email" className="form-label">
          Email address
        </label>
        <input
          type="email"
          className="form-control"
          id="email"
          value={input.email}
          onChange={(ev) => {
            const cloneInput = { ...input };
            cloneInput.email = ev.target.value;
            setInput(cloneInput);
          }}
        ></input>
        {/* <small className="text-danger">We'll never share your email with anyone else.</small> */}
      </div>

      <div className="mb-3">
        <label htmlFor="username" className="form-label">
          Username
        </label>
        <input
          type="text"
          className="form-control"
          id="username"
          onChange={(ev) => {
            const cloneInput = { ...input };
            cloneInput.username = ev.target.value;
            setInput(cloneInput);
          }}
        ></input>
      </div>

      <div className="mb-3">
        <label htmlFor="phoneNumber" className="form-label">
          Phone Number
        </label>
        <input type="text" className="form-control" id="phoneNumber"></input>
      </div>
      <button type="submit" className="btn btn-primary">
        Submit
      </button>
    </form>
  );
}
export default TodoInput;
