import { TodoContext } from "../../contexts/TodoContext";
import PageList from "./PageList";
import { useContext } from "react";

function Pagination(props) {
  const ctx = useContext(TodoContext);
  // Destructuring object
  const { setPage, todoList, filteredTodoList } = props;

  return (
    <div className="mt-2 d-flex align-items-baseline justify-content-between">
      <small className="text-muted">
        Showing {(ctx.page.currentPage - 1) * ctx.page.pagelimit + 1} to{" "}
        {filteredTodoList.length < ctx.page.currentPage * ctx.page.pagelimit
          ? filteredTodoList.length
          : ctx.page.currentPage * ctx.page.pagelimit}{" "}
        of {todoList.length} entries
      </small>

      <PageList page={page} setPage={setPage} filteredTodoList={filteredTodoList}></PageList>
    </div>
  );
}
export default Pagination;
