import Todo from "./Todo";
import { useState } from "react";
function TodoList(props) {
  const { todoList, removeTodo, updateTodo, searchStatus } = props;

  const CompletedTodoList = todoList.filter((item) => item.completed);
  const PendingTodoList = todoList.filter((item) => !item.completed);

  const [displayTodoList, setDisplayTodoList] = useState(todoList);

  if (searchStatus === "ALL") setDisplayTodoList([...todoList]);
  else if (searchStatus === "COMPLETED") setDisplayTodoList([...CompletedTodoList]);
  else if (searchStatus === "PENDING") setDisplayTodoList([...PendingTodoList]);

  return (
    <ul className="list-group shadown mt-3">
      {displayTodoList.map((item) => {
        return (
          <Todo
            key={item.id}
            title={item.title}
            completed={item.completed}
            id={item.id}
            removeTodo={removeTodo}
            updateTodo={updateTodo}
          ></Todo>
        );
      })}
    </ul>
  );
}
export default TodoList;
