import Button from "../ui/Button";
function Todo(props) {
  const { title = "Task", completed } = props;
  return (
    <li className="list-group-item d-flex  align-items-center bd-callout bd-callout-warning">
      <span className="flex-grow-1" role="button">
        {title}
      </span>
      <div className="btn-group">
        <Button color="outline-info">
          <li className="fa-solid fa-toggle-on" />
        </Button>
        <Button color="danger">
          <li className="fa-regular fa-trash-can" />
        </Button>
      </div>
    </li>
  );
}
export default Todo;
