import { useState } from "react";
import Button from "../ui/Button";
import TodoInput from "./TodoInput";
function Todo(props) {
  const { title, completed, id, removeTodo, updateTodo } = props;
  const [isEditing, setIsEditing] = useState(false);
  return (
    <li
      className={`list-group-item d-flex  align-items-center bd-callout ${
        completed ? "bd-callout-success" : "bd-callout-warning"
      }`}
    >
      <TodoInput id={id} title={title} />

      <span className="flex-grow-1" role="button">
        {title}
      </span>
      <div className="btn-group">
        <Button color="outline-info" onClick={() => updateTodo({ completed: !completed }, id)}>
          <li className={`fa-solid ${completed ? "fa-toggle-on" : "fa-toggle-off"}`} />
        </Button>
        <Button color="danger" onClick={() => removeTodo(id)}>
          <li className="fa-regular fa-trash-can" />
        </Button>
      </div>
    </li>
  );
}
export default Todo;
