function RadioButton(props) {
  return (
    <>
      <input
        type="radio"
        class="btn-check"
        name={props.name}
        id={props.id}
        onChange={props.onChange}
        autoComplete="off"
        checked={props.id.toUpperCase() === "ALL" ? true : null}
      />
      <label className={`btn btn-${props.color || "outline-primary"} shadow-none`} htmlFor={props.id}>
        {props.children}
      </label>
    </>
  );
}
export default RadioButton;
