import { createContext, useState, useEffect } from "react";
import axios from "axios";
import { v4 as uuidv4 } from "uuid";

const TodoContext = createContext();

function TodoContextProvider({ children }) {
  const [todoList, setTodoList] = useState([]);

  const createTodo = (title) => {
    axios
      .post("http://localhost:8080/todos", { title, completed: false })
      .then((res) => setTodoList([...todoList].unshift(res)))
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    axios
      .get("http://localhost:8080/todos")
      .then((res) => setTodoList(res.data.todos))
      .catch((err) => console.log(err));
  }, []);

  return <TodoContext.Provider value={{ todoList, setTodoList, createTodo }}>{children}</TodoContext.Provider>;
}

export { TodoContext, TodoContextProvider };
