function RadioButton(props) {
  return (
    <>
      <input type="radio" class="btn-check" name={props.name} id={props.id} />
      <label className={`btn btn-${props.color} shadow-none`} htmlFor={props.id}>
        {props.children}
      </label>
    </>
  );
}
export default RadioButton;
