import Todo from "./Todo";
function TodoList(props) {
  const { todoList, setTodoList } = props;

  const deleteTodo = (id) => {
    const oldTodolist = [...todoList];
    const indexDelete = oldTodolist.findIndex((item) => item.id === id);
    oldTodolist.splice(indexDelete, 1);
    setTodoList(oldTodolist);
  };

  return (
    <ul className="list-group shadown mt-3">
      {todoList.map((item) => {
        return (
          <Todo key={item.id} title={item.title} completed={item.completed} id={item.id} deleteTodo={deleteTodo}></Todo>
        );
      })}
    </ul>
  );
}
export default TodoList;
