import { v4 as uuidv4 } from "uuid";
import { useState } from "react";

import TodoInput from "./components/todo-list/TodoInput";
import Filter from "./components/filter/Filter";
import PageLimit from "./components/page-limit/PageLimit";
import TodoList from "./components/todo-list/TodoList";
import Pagination from "./components/pagination/Pagination";

function genList(number) {
  let arr = [];
  for (let i = 0; i < number; i++) {
    arr.push({ title: `Task ${i + 1}`, completed: i % 2 == 0 ? true : false, id: uuidv4() });
  }
  return arr;
}

const initialTodoList = [...genList(108)];

function App() {
  const [todoList, setTodoList] = useState(initialTodoList);
  const [searchStatus, setSearchStatus] = useState(null);
  const [searchInput, setSearchInput] = useState("");

  const searchTodoList = todoList.filter((el) => el.title.toUpperCase().startsWith(searchInput.toUpperCase()));
  let filteredTodoList = searchTodoList.filter((el) => searchStatus === null || el.completed === searchStatus);

  const [page, setPage] = useState({
    pagelimit: 10,
    currentPage: 1,
    pageBackNext: null
  });

  const filteredTodoListLimit = filteredTodoList.slice(0, page.pagelimit);

  const createTodo = (title) => {
    const newTodo = { title, completed: false, id: uuidv4() };
    const oldTodolist = [...todoList];
    oldTodolist.unshift(newTodo);
    setTodoList(oldTodolist);
  };

  const removeTodo = (id) => {
    const idx = todoList.findIndex((item) => item.id === id);
    if (idx !== -1) {
      const cloneTodoList = [...todoList];
      cloneTodoList.splice(idx, 1);
      setTodoList(cloneTodoList);
    }
  };

  const updateTodo = (newValue, id) => {
    const idx = todoList.findIndex((item) => item.id === id);
    if (idx !== -1) {
      const cloneTodoList = [...todoList];
      cloneTodoList[idx] = { ...todoList[idx], ...newValue }; // เป็นการ Merger object โดยใช้ Destructuring โดยมันจะทำการ Update เฉพาะ key ที่ส่งเข้ามา
      setTodoList(cloneTodoList);
    }
  };

  const changeSearchStatus = (value) => {
    setSearchStatus(value);
  };

  return (
    <div className="container max-w-xs pt-5">
      <TodoInput createTodo={createTodo} />
      <Filter
        changeSearchStatus={changeSearchStatus}
        searchStatus={searchStatus}
        searchInput={searchInput}
        setSearchInput={setSearchInput}
      />
      <PageLimit page={page} setPage={setPage} />
      <TodoList
        filteredTodoListLimit={filteredTodoListLimit}
        setFilteredTodoList={filteredTodoList}
        setTodoList={setTodoList}
        removeTodo={removeTodo}
        updateTodo={updateTodo}
        searchStatus={searchStatus}
      />
      <Pagination
        page={page}
        setPage={setPage}
        todoList={todoList}
        filteredTodoListLimit={filteredTodoListLimit}
        searchTodoList={searchTodoList}
      />
    </div>
  );
}

export default App;
