function PageItem(props) {
  // Destructuring object
  const { active, disabled, children, page, setPage, id } = props;

  const setCurrentPage = (ev) => {
    const clonePage = { ...page };
    if (ev.target.innerText) clonePage.currentPage = +ev.target.innerText;
    setPage(clonePage);
  };

  const setBackNextPage = (ev) => {
    const clonePage = { ...page };
    clonePage.pageBackNext = ev.target.id === "back" ? false : ev.target.id === "next" ? true : null;
    setPage(clonePage);
  };

  return (
    <li className={`page-item ${active ? "active" : ""} ${disabled ? "disabled" : ""}`}>
      <button id={id || ""} className="page-link shadow-none" onClick={id ? setBackNextPage : setCurrentPage}>
        {children}
      </button>
    </li>
  );
}
export default PageItem;
