import { useState } from "react";
import Button from "../ui/Button";

function TodoInput(props) {
  const [todoInput, setTodoInput] = useState();
  return (
    <>
      <div className="input-group shadow">
        <input
          type="text"
          className="form-control"
          placeholder="Enter new todo"
          value={todoInput}
          onChange={(ev) => setTodoInput(ev.target.value)}
        ></input>
        <Button
          color="success"
          onClick={() => {
            if (todoInput) props.createTodo(todoInput);
          }}
        >
          <i className="fa-solid fa-plus"></i>
        </Button>
        <Button color="outline-secondary" onClick={() => setTodoInput("")}>
          <i className="fa-solid fa-xmark"></i>
        </Button>
      </div>
      <small className={`text-danger ${todoInput ? "overflow-hidden" : "overflow-none"}`}>Task is required</small>
    </>
  );
}
export default TodoInput;
